import React, { useState } from "react";
import "./ExpenseItem.css";
import ExpenseDate from "./ExpenseDate";
import Card from "../UI/Card";
import ExpensesFilter from "../NewExpense/ExpensesFilter";

function ExpenseItem(props) {
	const [title, setTitle] = useState(props.title);
	const [yearFilter, setYearFilter] = useState(props.yearFilter);

	const clickHandler = () => (setTitle("updated!"), console.log(title));
	const filterHandler = (value) => {
		return setYearFilter(value);
	};

	// const setYearFilter = (year) => {yearFilter = year;
	// if }
	const setYearHandler = (props) => console.log(props);

	return (
		<div>
			<Card className="expense-item">
				<ExpensesFilter onSetYear={setYearHandler} />
				<ExpenseDate date={props.date} />
				<div className="expense-item__description">
					<h2>{title}</h2>
				</div>
				<div className="expense-item__price">${props.amount}</div>
				<button onClick={clickHandler}>Click me!</button>
			</Card>
		</div>
	);
}

export default ExpenseItem;
